mapper.select:
select
  solution_id, problem_id, content, create_time, status_code, user_id, title, modify_time
from oj.solution


        and solution_id = #{solutionId}


        and problem_id = #{problemId}


        and content = #{content}


        and create_time = #{createTime}


        and status_code = #{statusCode}


        and user_id = #{userId}


        and title = #{title}


        and modify_time = #{modifyTime}

select * from solution s
join `user` u on s.user_id = u.user_id
join problem p on s.problem_id = p.problem_id

    1 = 1

        and s.solution_id = #{solutionId}


        and s.problem_id = #{problemId}


        and s.user_id = #{userId}

    and s.statuc_code = #{status}
